var sessions = require('sessions'),
	path = require('path'),
	fs = require('fs'),
	_ = require('underscore'),
	sessions = require('sessions');

var booksHtml = path.join(__dirname, "../html/books.html");

/* exports */
module.exports.process = function(req, res) {
	throwBooksHtml(req, res);
}

/* functions */
function throwBooksHtml(req, res) {
	fs.readFile(booksHtml, {encoding: 'utf8'}, function(err, content) {
		if(err) throw err;
		
		var login = sessions.get(req, 'login');
		content = _.template(content, {login: login});

		res.statusCode = 200;
		res.end(content);
	});
}
