var fs = require('fs');

/* export */
module.exports.redirectTo = redirectTo;
module.exports.throwFile = throwFile;

/* functions */
function redirectTo(res, where) {
		res.writeHeader(301, {
			"Location": where,
		});
		res.end();
}

function throwFile(req, res, filePath) {
	fs.readFile(filePath, {encoding: 'utf8'}, function(err, content) {
		if(err) throw err;

		res.statusCode = 200;
		res.end(content);
	});
}
